package vankrimpen.dustin;

import java.util.Arrays;

/*
 * Given two strings, write a method to decide if one is a permutation of the other.
 */
public class Permutations {
	
	// Method A sorts the strings as char arrays and checks if they are equal 
	public static boolean isPermutationA(String s1, String s2){
		// easy check - if not same length return false 
		if(s1.length() != s2.length()) {
			return false;
		}
		char[] s1Chars = s1.toCharArray();
		char[] s2Chars = s2.toCharArray();
		Arrays.sort(s1Chars);
		Arrays.sort(s2Chars);
		
		if(Arrays.equals(s1Chars, s2Chars)) {
			return true;
		}
		return false;
		
	}
	// Method B maps each character of the first string to a running count of it's frequency
	// It then goes through the second array, subtracting char counts from the map as they appear
	// If the method makes it past iterating through the second array without returning false, then it is true  
	public static boolean isPermutationB(String s1, String s2) {
		if(s1.length() != s2.length()) {
			return false;
		}
		
		int[] map = new int[128];
		char[] s1Chars = s1.toCharArray();
		char[] s2Chars = s2.toCharArray();
		
		for(char c: s1Chars) {
			map[c] += 1;
		}
		
		for(char c : s2Chars) {
			if(map[c] == 0) {
				return false;
			}
			map[c] -= 1;
		}
		
		return true;
	}

	public static void main(String[] args) {
		// Test methods
		String s1 = "earth";
		String s2 = "heart";
		
		boolean testA = isPermutationA(s1, s2);
		boolean testB = isPermutationB(s1, s2);
		System.out.printf("Evaluating Strings: \"%s\", \"%s\"\n",s1,s2);
		System.out.printf("Method A result: %b\nMethod B result: %b\n", testA, testB);
		
		System.out.println("--------------------");
		
		s1 = "hearts";
		s2 = "earth";
		testA = isPermutationA(s1, s2);
		testB = isPermutationB(s1, s2);
		System.out.printf("Evaluating Strings: \"%s\", \"%s\"\n",s1,s2);
		System.out.printf("Method A result: %b\nMethod B result: %b\n", testA, testB);
		
		System.out.println("--------------------");
		
		// Compare performance 
		long timeAstart = System.currentTimeMillis();
		testA = isPermutationA("",
				"");
		long timeAend = System.currentTimeMillis();
		long timeA = timeAend - timeAstart;
		
		long timeBstart = System.currentTimeMillis();
		testB = isPermutationB("",
				"");
		long timeBend = System.currentTimeMillis();
		long timeB = timeBend - timeBstart;
		
	
		System.out.println("Performance comparison using Strings of ~5000 characters that are permutations of each other:");
		System.out.printf("Method A result: %b\tTime to finish: %d milliseconds\n",testA,timeA);
		System.out.printf("Method B result: %b\tTime to finish: %d milliseconds\n",testB,timeB);
		
	}
	
}
